const themeToggle=document.getElementById("theme-toggle");const themeIcon=themeToggle.querySelector("i");const currentTheme=localStorage.getItem("theme")||"light";document.documentElement.classList.toggle("dark",currentTheme==="dark");updateThemeIcon(currentTheme);themeToggle.addEventListener("click",()=>{const isDark=document.documentElement.classList.toggle("dark");localStorage.setItem("theme",isDark?"dark":"light");updateThemeIcon(isDark?"dark":"light")});function updateThemeIcon(theme){if(theme==="dark"){themeIcon.classList.remove("fa-moon");themeIcon.classList.add("fa-sun")}else{themeIcon.classList.remove("fa-sun");themeIcon.classList.add("fa-moon")}}document.addEventListener("DOMContentLoaded",function(){const mobileMenuToggle=document.getElementById("mobile-menu-toggle");const mobileMenu=document.getElementById("mobile-menu");const mobileMenuOverlay=document.getElementById("mobile-menu-overlay");function openMobileMenu(){console.log("Opening mobile menu");mobileMenu.classList.add("open");mobileMenuOverlay.classList.add("open");mobileMenuToggle.classList.add("open");document.body.style.overflow="hidden"}function closeMobileMenu(){console.log("Closing mobile menu");mobileMenu.classList.remove("open");mobileMenuOverlay.classList.remove("open");mobileMenuToggle.classList.remove("open");document.body.style.overflow=""}document.addEventListener("click",function(e){if(e.target.closest("#mobile-menu-toggle")){openMobileMenu()}if(e.target.closest("#mobile-menu")||e.target===mobileMenuOverlay){closeMobileMenu()}});document.querySelectorAll(".mobile-menu-link").forEach(link=>{link.addEventListener("click",closeMobileMenu)});document.addEventListener("keydown",e=>{if(e.key==="Escape"&&mobileMenu.classList.contains("open")){closeMobileMenu()}})});document.querySelectorAll('a[href^="#"]').forEach(anchor=>{anchor.addEventListener("click",function(e){e.preventDefault();const targetId=this.getAttribute("href");if(targetId==="#")return;const targetElement=document.querySelector(targetId);if(targetElement){window.scrollTo({top:targetElement.offsetTop-80,behavior:"smooth"})}})});function animateCounter(element){const target=parseInt(element.getAttribute("data-count"));const duration=2e3;const step=target/(duration/16);let current=0;const timer=setInterval(()=>{current+=step;if(current>=target){element.textContent=target+"+";clearInterval(timer)}else{element.textContent=Math.floor(current)}},16)}const observerOptions={threshold:.1,rootMargin:"0px 0px -50px 0px"};const observer=new IntersectionObserver(entries=>{entries.forEach(entry=>{if(entry.isIntersecting){const statNumbers=entry.target.querySelectorAll(".stat-number");if(statNumbers.length>0){statNumbers.forEach(animateCounter)}entry.target.classList.add("loaded")}})},observerOptions);document.querySelectorAll(".section").forEach(section=>{observer.observe(section)});const contactForm=document.getElementById("contact-form");if(contactForm){contactForm.addEventListener("submit",function(e){e.preventDefault();const formData=new FormData(this);const name=document.getElementById("name").value;const email=document.getElementById("email").value;const subject=document.getElementById("subject").value;const message=document.getElementById("message").value;alert(`Thank you for your message, ${name}! I'll get back to you soon.`);this.reset()})}window.addEventListener("scroll",function(){const header=document.querySelector(".header");if(window.scrollY>100){header.style.background="rgba(255, 255, 255, 0.1)";header.style.backdropFilter="blur(20px)";header.style.boxShadow="0 4px 24px rgba(0, 0, 0, 0.1)"}else{header.style.background="rgba(255, 255, 255, 0.05)";header.style.backdropFilter="blur(20px)";header.style.boxShadow="none"}});document.addEventListener("DOMContentLoaded",function(){const animatedElements=document.querySelectorAll(".glass-card, .btn, .skill-tag, .timeline-content, .contact-item, .nav-link, .theme-toggle, .social-link, .contact-icon, .skill-icon");animatedElements.forEach((element,index)=>{element.classList.add("loading");setTimeout(()=>{element.classList.remove("loading");element.classList.add("loaded")},100+index*50)})});